import { useMemo } from 'react';
import Ball from './Ball';

export default function Flask({onClick, ball, config, total, isSuccess}) {
    const currentTotal = useMemo(()=>{
        return ball.reduce((a, b) => a + b, 0)
    }, [ball]);
    return (
        <div className='flask' onClick={onClick}>
            <div className='flaskChild' style={{minWidth: 25, minHeight: config.ballCount * 34 }}>
                <div className='flaskAbs'>
                    {ball.map( (i,index)=> <Ball key={i*index} num={i}/>)}
                </div>
            </div>
            <div>
                <span>{currentTotal}</span>/           
                <span className={`total ${isSuccess ? 'done': 'empty'}`}>{total}</span>               
            </div>
            
        </div>
    );
}
