import { useCallback, useState } from 'react';
import { View } from 'react-native';
import Game from './src/Containers/Game';
import Start from './src/Containers/Start';
import './index.css';
import Header from './src/Components/Header';
import Footer from './src/Components/Footer';

export default function App() {
  const [level, setLevel] = useState(1);
  console.log(level)
  const [currentLevel, setCurrentLevel] = useState(0);
  const onStart = useCallback(()=>{
    setCurrentLevel(level);
  }, [setCurrentLevel]);

  const onNext = useCallback(()=>{
    setCurrentLevel(currentLevel+1);
  }, [setCurrentLevel, currentLevel ]);

  return (
    <div className="game-container">
      <audio controls>  
        <source src="./assets/sound.mp3" type="audio/mpeg" />  
      </audio>  
      <audio controls id="audio" src="./assets/sound.mp3">
      </audio>
        <Header level={currentLevel} />
        <div className='content'>
          {!currentLevel && <Start level={level} onStart={onStart} />}
          {!!currentLevel && <Game onNext={onNext} level={currentLevel}/>}
        </div>
        <Footer />
    </div>
  );
}
