import { StyleSheet, View } from "react-native";

export default function Ball({num, clickedBall}) {
    return (
        <View className={`ball ${clickedBall && 'clickedBall'}`}>
           {num}
        </View>
    );
}

const styles = StyleSheet.create({
    ball: {
        borderRadius: 50,
        borderStyle: 'solid',
        borderWidth: 1,
        padding: 5,
        margin: 3
    },
    clickedBall: {
        position: 'relative',
        marginTop: 20,
        padding: 5,
        borderLeftWidth: 1,
        borderStyle: 'solid',
        borderBottomWidth: 1,
        borderRightWidth: 1
    }
});
