import React from 'react';
import { useCallback, useEffect, useState } from 'react/cjs/react.development';
import SettingsModal from './SettingsModal';
import { Audio } from 'expo-av';


export default function Header({level}) {
    const [modalVisible, setModalVisible] = useState(false);
    const [sound, setSound] = useState(localStorage.getItem('sound') === 'true');
    const [effect, setEffect] = useState(localStorage.getItem('effect') === 'true');

    async function playSound() {
        const { sound } = await Audio.Sound.createAsync(
           require('assets/sound.mp3')
        );
        await sound.playAsync(); 
    }

    const onSound = useCallback(() => {
        localStorage.setItem('sound', !sound);
        setSound(!sound);
    }, [setSound, sound]);

    const onEffect = useCallback(() => {
        localStorage.setItem('effect', !effect);
        setEffect(!effect);
    }, [setEffect, effect]);

    const soundChangeHandler = useCallback(()=> {
        playSound();
    }, [sound, playSound]);

    useEffect(soundChangeHandler, [sound]);

    return (
        <div>
            <div className='header'>
                <div onClick={() => setModalVisible(true)}>settings</div>
                <div>{level ? level: ''}</div>
                <div>x</div>
            </div>
            {modalVisible && <SettingsModal 
                sound={sound}
                effect={effect}
                onSound={onSound}
                onEffect={onEffect} 
                setModalVisible={setModalVisible} 
                modalVisible={modalVisible}
              />}
        </div>
    );
}
