import { useMemo } from 'react';
import { StyleSheet, View } from 'react-native';
import Ball from './Ball';

export default function Flask({clickedBall, onClick, ball, config, total, isSuccess}) {
    const currentTotal = useMemo(()=>{
        return ball.reduce((a, b) => a + b, 0)
    }, [ball]);
    return (
        <View style={styles.flask} onClick={onClick}>
            <View style={{...styles.flaskChild, minWidth: 25, minHeight: config.ballCount * 34 }}>
                <View style={styles.flaskAbs}>
                    {ball.map( (i,index)=> <Ball clickedBall={clickedBall && !index} key={i*index} num={i}/>)}
                </View>
            </View>
            <View>
                <span>{currentTotal}</span> {!!currentTotal && !!total && '/'}        
                <span className={`total ${isSuccess ? 'done': 'empty'}`}>{total}</span>               
            </View>
            
        </View>
    );
}

const styles = StyleSheet.create({
    flask: {
        float: 'left',
        marginRight: '10px'
    },
    flaskChild: {
        position: 'relative',
        marginTop: 20,
        padding: '5px',
        borderLeftWidth: 1,
        borderBottomWidth: 1,
        borderRightWidth: 1
    },
    flaskAbs: {
        position: 'absolute',
        bottom: 0
    }
});
