import { useCallback, useEffect, useMemo, useState } from 'react';
import { View, ActivityIndicator } from 'react-native';
import GameClass from '../Classes/Game';

export default function Game({level}) {
    const [data, setData] =useState({});
    const [game, setGame] = useState(false);

    const init = useCallback(()=> {
        const gameClass = new GameClass();
        gameClass.start(level);
        setGame(gameClass);
    }, [level, setGame]);

    const dataHandler = useCallback(()=> {
        if (game){
            setData(game.data)
        }
    }, [game, setData]);

    const viewData = useMemo(()=> {
        if (data?.mixedData){
            console.log(data?.mixedData)
        } else return <ActivityIndicator />;
    }, [data]);

    useEffect(init, [level]);
    useEffect(dataHandler, [game]);

    return (
        <View>
            {viewData}
        </View>
    );
}
